package generator

const (
	FileTemplate = `
{{$data := .}}
package {{$data.PkgName}}

// Code generated by easyhide. DO NOT EDIT.

{{if $data.EasyJSON}}
//go:generate easyjson
{{end}}

import (
	json "encoding/json"

	easyhide "github.com/denis96z/go-easyhide/pkg/easyhide"
)

{{range $idx, $tp := $data.Types}}
	{{$tp.TypeCode}}

	{{$tp.FuncCode}}
{{end}}
`
)
